import React from 'react';

class Day extends React.Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    render() {
        return  <div className="day">{this.props.day}</div> ;
    }
}

class Calendar extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            date: new Date(),
        }
    }

    months = [
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July",
        "August",
        "September",
        "October",
        "November",
        "December",
    ]

    componentDidMount() {
        let day = this.state.date.getDate();
        let month = this.state.date.getMonth();
        let year = this.state.date.getFullYear();
        let daysInMonth = this.getNumberOfDays(this.state.date);
        let arrOfDays = [];
        for (let i = 1; i <= daysInMonth; i++) {arrOfDays.push(i);};
        this.setState ({ 
            day: day,
            month: month,
            year: year,
            daysInMonth: daysInMonth,
            arrOfDays: arrOfDays,
        })
    }

    getNumberOfDays(dateObject) {
        let dateToChange = new Date(dateObject);
        let number = dateToChange.getDate();
        dateToChange.setDate(dateToChange.getDate() + 1);
        while (dateToChange.getMonth() == dateObject.getMonth()) {
            number++;
            dateToChange.setDate(dateToChange.getDate() + 1);
        }
        return number;
    }


    render() {
        return (
            <div className="calendar">
                {  this.state.arrOfDays.map(day => <Day day={day} />)}
            </div>
        )
    }

}

export default Calendar
