import React from 'react';

class Calendar extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            date: new Date(),
        }
    }

    componentDidMount() {
        this.setState ({
            day: this.state.date.getDate(),
            month: this.state.date.getMonth(),
            year: this.state.date.getFullYear(),
        })
    }

    months = [
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July",
        "August",
        "September",
        "October",
        "November",
        "December",
    ]

    render() {
        return (
            <div className="all-holder">
                <div className="day-holder"><h1 className="day">{this.state.day}</h1></div>
                <div className="month-holder"><h3 className="month">{this.months[this.state.month]}</h3></div>
                <div className="year-holder"><h3 className="year">{this.state.year}</h3></div>
            </div>
        )
    }

}

export default Calendar
